function() {
  // Değişkenin mevcut olup olmadığını kontrol et
  var price = {{ecommerce.detail.products.0.price}}; // GTM değişkeni burada kullanılmalı

  // Eğer değer mevcut değilse veya sayıya çevrilemiyorsa null döndür
  if (!price || isNaN(price)) {
    return null;
  }

  // String değeri sayıya çevir ve döndür
  return parseFloat(price);
}

