function() {
  var ca = decodeURIComponent(document.cookie).split(';');
  var growthAllCookie = "";
  for (var i = 0; i < ca.length; i++) {
    var c = ca[i].trim();
    // _gtm_exp veya _gtm_exp_something ile başlıyorsa
    if (c.startsWith("_gtm_exp=") || c.startsWith("_gtm_exp_")) {
      var eqIndex = c.indexOf("=");
      if (eqIndex > -1) {
        growthAllCookie += c.substring(eqIndex + 1) + ", ";
      }
    }
  }
  return growthAllCookie;
}

